targets.expr:
1-(
  sum without (feature, title, sli_class) (sum_over_time(
    label_replace(
       slo:sli_error:ratio_rate1h{sloth_service="${service}",sloth_slo="${slo}"}, "slo_version", "0", "slo_version", ""
    )[${budget_window_days}d:1h]
  )) 
  / on(sloth_id, slo_version)
  sum without (feature, title, sli_class) (
    label_replace(
      slo:error_budget:ratio{sloth_service="${service}",sloth_slo="${slo}"},
      "slo_version", "0", "slo_version", ""
    ) *on() group_left() (24 * ${budget_window_days})
  )
)

